import styled from 'styled-components';

export const RestaurantHead = styled.div`
  padding: 20px;
  width: 100%;
  display: flex;
`;

export const RestaurantImage = styled.img`
  width: 240px;
`;

export const RestaurantData = styled.div`
  display: flex;
  flex-direction: column;
  padding-left: 30px;

  p {
    margin-top: 10px;
    margin-bottom: 10px;
  }
`;
